// Parse incoming request
const body = $json.body ?? $json;
const transcript = String(body.transcript || body.message || '').trim();
const actor = String(body.actor || 'morgan');
const session_id = body.session_id || `route_${Date.now()}`;
const request_id = body.request_id || `req_${Date.now()}`;

if (!transcript) {
  return { ok: false, error: 'No transcript provided' };
}

return {
  ok: true,
  transcript,
  actor,
  session_id,
  request_id,
  timestamp: new Date().toISOString()
};

// Process routing result
const inputData = $('Parse Request').first().json;
const routeResult = $json;

const primaryAgent = routeResult.primary_agent;
const requiresConfirmation = routeResult.requires_confirmation;
const matchedAgents = routeResult.matched_agents || [];

let action = 'direct_response';
let targetAgent = null;
let routingMessage = routeResult.routing_prompt || 'I\'ll handle this directly.';

if (primaryAgent) {
  targetAgent = primaryAgent;
  action = requiresConfirmation ? 'confirm_required' : 'route_to_agent';
}

return {
  ok: true,
  request_id: inputData.request_id,
  transcript: inputData.transcript,
  action,
  target_agent: targetAgent,
  matched_agents: matchedAgents.map(a => a.agent_id),
  requires_confirmation: requiresConfirmation,
  routing_message: routingMessage,
  session_id: inputData.session_id,
  timestamp: new Date().toISOString()
};

// Generate confirmation request
const data = $('Process Route').first().json;

return {
  ok: true,
  request_id: data.request_id,
  action: 'pending_confirmation',
  target_agent: data.target_agent,
  confirmation_prompt: `This command will be routed to ${data.target_agent?.display_name || 'an agent'}. ${data.target_agent?.requires_confirmation ? 'This action requires confirmation. Say "Confirm and proceed" to continue.' : ''}`,
  transcript: data.transcript,
  session_id: data.session_id,
  timestamp: new Date().toISOString()
};

// Format final response
const inputData = $('Process Route').first().json;
const result = $json;

return {
  ok: true,
  request_id: inputData.request_id,
  transcript: inputData.transcript,
  response_text: result.response_text || result.confirmation_prompt || 'Command processed.',
  action: inputData.action,
  target_agent: inputData.target_agent?.display_name || 'orchestrator',
  conversation_id: result.conversation_id || inputData.session_id,
  timestamp: new Date().toISOString()
};

