// MYCA Jarvis - Unified AI Assistant Interface
// This is the main entry point for all MYCA interactions

const input = $input.first().json;

// Extract request parameters
const message = input.message || input.text || input.transcript || '';
const actor = input.actor || 'morgan';
const sessionId = input.session_id || input.conversation_id || `jarvis_${Date.now()}`;
const wantAudio = input.want_audio !== false;
const context = input.context || {};

// Quick command detection for instant responses
const quickCommands = {
  'hello': 'greeting',
  'hi': 'greeting',
  'hey myca': 'greeting',
  'status': 'system_status',
  'help': 'help',
  'what can you do': 'capabilities',
  'thanks': 'acknowledge',
  'thank you': 'acknowledge',
  'bye': 'farewell',
  'goodbye': 'farewell'
};

const lowerMessage = message.toLowerCase().trim();
let quickCommand = null;
for (const [trigger, cmd] of Object.entries(quickCommands)) {
  if (lowerMessage === trigger || lowerMessage.startsWith(trigger + ' ') || lowerMessage.endsWith(' ' + trigger)) {
    quickCommand = cmd;
    break;
  }
}

return {
  json: {
    message: message,
    actor: actor,
    session_id: sessionId,
    want_audio: wantAudio,
    quick_command: quickCommand,
    context: context,
    timestamp: new Date().toISOString()
  }
};

// Handle quick commands with instant responses
const input = $input.first().json;
const cmd = input.quick_command;

const responses = {
  greeting: "Hello! I'm MYCA, pronounced 'my-kah'. I'm your Mycosoft Autonomous Cognitive Agent, here to help you manage systems, access information, and get things done. What would you like me to help with?",

  system_status: "All systems are operational. The MAS orchestrator is running with 42 agents registered. Voice services are online. How can I assist you further?",

  help: "I can help you with many things: manage Proxmox VMs and containers, control UniFi network devices, access NAS storage, run code and scripts, manage projects and tasks, search the web, check calendars and emails, generate reports, and much more. Just tell me what you need.",

  capabilities: "My capabilities include: Infrastructure management for Proxmox, UniFi, and NAS. Business operations like project management, CRM, and reporting. Productivity tools for calendar, email, and files. Development tools for code execution and deployment. Plus integration with over 40 specialized agents for mycology research, finance, sales, and more.",

  acknowledge: "You're welcome! Is there anything else I can help you with?",

  farewell: "Goodbye! Feel free to call on me whenever you need assistance. This is MYCA, signing off."
};

const responseText = responses[cmd] || responses.greeting;

return {
  json: {
    status: 'success',
    session_id: input.session_id,
    response_text: responseText,
    quick_response: true,
    command: cmd,
    want_audio: input.want_audio
  }
};

// Extract response from brain
const input = $input.first().json;
const originalData = $('Parse Input').first().json;

let responseText = '';

if (input.response_text) {
  responseText = input.response_text;
} else if (input.result) {
  responseText = typeof input.result === 'string' ? input.result : JSON.stringify(input.result, null, 2);
} else if (input.data && input.data.result) {
  responseText = input.data.result;
} else {
  responseText = 'I processed your request but have no specific response.';
}

return {
  json: {
    status: 'success',
    session_id: originalData.session_id,
    response_text: responseText,
    intent: input.intent || null,
    data: input.data || null,
    want_audio: originalData.want_audio
  }
};

// Merge responses from both paths
const quickResponse = $('Quick Response').first()?.json;
const brainResponse = $('Extract Response').first()?.json;

const response = quickResponse || brainResponse;

return {
  json: response
};

// Add audio to response
const textResponse = $('Merge Responses').first().json;
const audioData = $input.first().binary;

let audioBase64 = null;
if (audioData && audioData.data) {
  audioBase64 = audioData.data.toString('base64');
}

return {
  json: {
    ...textResponse,
    audio_base64: audioBase64,
    audio_mime: 'audio/mpeg'
  }
};

// Return text-only response
const response = $('Merge Responses').first().json;

return {
  json: {
    ...response,
    audio_base64: null,
    audio_mime: null
  }
};

// Final response formatting
const withAudio = $('Add Audio').first()?.json;
const textOnly = $('Text Only').first()?.json;

const response = withAudio || textOnly;

return {
  json: {
    status: 'success',
    session_id: response.session_id,
    response_text: response.response_text,
    intent: response.intent,
    data: response.data,
    audio_base64: response.audio_base64,
    audio_mime: response.audio_mime,
    timestamp: new Date().toISOString()
  }
};

