// MYCA Master Brain - Intent Classification & Context Management
const input = $input.first().json;

// Extract request details
const message = input.message || input.text || input.transcript || '';
const actor = input.actor || 'user';
const sessionId = input.session_id || input.conversation_id || `session_${Date.now()}`;
const context = input.context || {};

// Intent classification patterns
const intents = {
  // System & Infrastructure
  system_status: /\b(status|health|check|how.*doing|running|online)\b/i,
  infrastructure: /\b(proxmox|vm|virtual|server|container|docker|kubernetes)\b/i,
  network: /\b(network|unifi|wifi|internet|connection|firewall|router|switch)\b/i,
  storage: /\b(nas|storage|disk|backup|file.*server|synology|truenas)\b/i,

  // Productivity & Business
  calendar: /\b(calendar|schedule|meeting|appointment|event|remind|tomorrow|today|week)\b/i,
  email: /\b(email|mail|inbox|send.*message|reply|compose)\b/i,
  tasks: /\b(task|todo|project|asana|deadline|assign|sprint|ticket)\b/i,
  notes: /\b(note|notion|document|write.*down|remember|save.*this)\b/i,

  // Information & Research
  search: /\b(search|find|look.*up|google|research|what.*is|who.*is|where.*is)\b/i,
  weather: /\b(weather|temperature|forecast|rain|sunny|cold|hot)\b/i,
  news: /\b(news|latest|headlines|updates|happening)\b/i,

  // Code & Development
  code: /\b(code|script|program|python|javascript|debug|deploy|git|github)\b/i,
  database: /\b(database|sql|query|data|postgres|redis|mongo)\b/i,
  api: /\b(api|endpoint|request|webhook|integration)\b/i,

  // Media & Files
  files: /\b(file|folder|download|upload|copy|move|delete|rename)\b/i,
  media: /\b(play|music|video|spotify|youtube|stream|podcast)\b/i,

  // Agents & AI
  agents: /\b(agent|mycology|research|financial|sales|dashboard|corporate)\b/i,
  ai: /\b(generate|create|write|compose|draft|summarize|translate|analyze)\b/i,

  // Control & Actions
  control: /\b(start|stop|restart|enable|disable|turn.*on|turn.*off|shutdown|reboot)\b/i,
  confirm: /\b(yes|no|confirm|cancel|approve|deny|proceed|abort)\b/i,
  help: /\b(help|how.*do|can.*you|what.*can|capabilities|commands)\b/i,

  // Conversation
  greeting: /\b(hello|hi|hey|good.*morning|good.*evening|greetings)\b/i,
  farewell: /\b(bye|goodbye|see.*you|later|exit|quit)\b/i,
  thanks: /\b(thank|thanks|appreciate|grateful)\b/i
};

// Classify intent
let detectedIntents = [];
let primaryIntent = 'general';
let confidence = 0.5;

for (const [intent, pattern] of Object.entries(intents)) {
  if (pattern.test(message)) {
    detectedIntents.push(intent);
  }
}

if (detectedIntents.length > 0) {
  primaryIntent = detectedIntents[0];
  confidence = Math.min(0.95, 0.6 + (detectedIntents.length * 0.1));
}

// Determine target workflow/action
const routingMap = {
  system_status: { target: 'system', action: 'status' },
  infrastructure: { target: 'system', action: 'infra' },
  network: { target: 'system', action: 'network' },
  storage: { target: 'system', action: 'storage' },
  calendar: { target: 'tools', action: 'calendar' },
  email: { target: 'tools', action: 'email' },
  tasks: { target: 'business', action: 'tasks' },
  notes: { target: 'tools', action: 'notes' },
  search: { target: 'tools', action: 'search' },
  weather: { target: 'tools', action: 'weather' },
  news: { target: 'tools', action: 'news' },
  code: { target: 'tools', action: 'code' },
  database: { target: 'system', action: 'database' },
  api: { target: 'tools', action: 'api' },
  files: { target: 'tools', action: 'files' },
  media: { target: 'tools', action: 'media' },
  agents: { target: 'agents', action: 'route' },
  ai: { target: 'ai', action: 'generate' },
  control: { target: 'system', action: 'control' },
  confirm: { target: 'confirm', action: 'resolve' },
  help: { target: 'help', action: 'list' },
  greeting: { target: 'chat', action: 'greet' },
  farewell: { target: 'chat', action: 'farewell' },
  thanks: { target: 'chat', action: 'acknowledge' },
  general: { target: 'chat', action: 'respond' }
};

const routing = routingMap[primaryIntent] || routingMap.general;

// Check for dangerous operations requiring confirmation
const dangerousPatterns = /\b(delete|remove|shutdown|reboot|destroy|wipe|format|drop|truncate)\b/i;
const requiresConfirmation = dangerousPatterns.test(message);

// Extract entities (basic NER)
const entities = {
  numbers: message.match(/\d+/g) || [],
  emails: message.match(/[\w.-]+@[\w.-]+\.\w+/g) || [],
  urls: message.match(/https?:\/\/[^\s]+/g) || [],
  times: message.match(/\d{1,2}:\d{2}|\d{1,2}\s*(am|pm)/gi) || [],
  dates: message.match(/\d{1,2}[\/\-]\d{1,2}[\/\-]\d{2,4}|today|tomorrow|yesterday|monday|tuesday|wednesday|thursday|friday|saturday|sunday/gi) || []
};

return {
  json: {
    session_id: sessionId,
    actor: actor,
    message: message,
    timestamp: new Date().toISOString(),

    // Intent classification
    intent: {
      primary: primaryIntent,
      all: detectedIntents,
      confidence: confidence
    },

    // Routing decision
    routing: {
      target: routing.target,
      action: routing.action,
      requires_confirmation: requiresConfirmation
    },

    // Extracted entities
    entities: entities,

    // Context for downstream
    context: {
      ...context,
      previous_intent: context.last_intent || null,
      turn_count: (context.turn_count || 0) + 1
    }
  }
};

// Format final response
const input = $input.first().json;
const originalData = $('Classify Intent').first().json;

let responseText = '';
let responseData = {};

// Extract response based on source
if (input.response_text) {
  responseText = input.response_text;
  responseData = input;
} else if (input.result) {
  responseText = typeof input.result === 'string' ? input.result : JSON.stringify(input.result);
  responseData = input;
} else if (input.message) {
  responseText = input.message;
  responseData = input;
} else {
  responseText = 'I processed your request.';
  responseData = input;
}

return {
  json: {
    status: 'success',
    session_id: originalData.session_id,
    intent: originalData.intent,
    response_text: responseText,
    data: responseData,
    timestamp: new Date().toISOString()
  }
};

