// Prepare audit record from workflow data
const crypto = require('crypto');

const input = $input.item.json;

// Hash sensitive data
const paramsHash = crypto.createHash('sha256')
  .update(JSON.stringify(input.params || {}))
  .digest('hex');

const responseHash = crypto.createHash('sha256')
  .update(JSON.stringify(input.response || {}))
  .digest('hex');

const auditRecord = {
  timestamp: new Date().toISOString(),
  request_id: input.request_id || $execution.id,
  actor: input.actor || 'system',
  integration: input.integration || 'unknown',
  action: input.action || 'unknown',
  category: input.category || null,
  params_hash: paramsHash,
  response_hash: responseHash,
  status: input.status || 'unknown',
  duration_ms: input.duration_ms || null,
  error_message: input.error_message || null,
  risk_level: input.risk_level || 'read_only',
  confirmed: input.confirmed || false,
  correlation_id: input.correlation_id || null,
  metadata: {
    execution_id: $execution.id,
    workflow_name: $workflow.name,
    n8n_version: $workflow.version
  }
};

return [{ json: auditRecord }];
