// Route to specific space/weather integration
const input = $input.item.json;
const integrationName = input.integration?.integration || 'unknown';
const action = input.command?.action || 'default';
const params = input.command?.params || {};

return [{ 
  json: { 
    ...input, 
    routing_node: integrationName,
    action: action,
    params: params
  } 
}];

// Fetch data based on integration type
const input = $input.item.json;
const integration = input.routing_node;
const action = input.action;
const params = input.params || {};

let result = null;
let error = null;

try {
  switch(integration) {
    case 'nasa_donki':
      result = { source: 'NASA DONKI', url: 'https://api.nasa.gov/DONKI', action, note: 'Connect HTTP node for live data' };
      break;
    case 'nasa_neo':
      result = { source: 'NASA NEO', url: 'https://api.nasa.gov/neo/rest/v1', action, note: 'Connect HTTP node for live data' };
      break;
    case 'noaa_swpc':
      result = { source: 'NOAA SWPC', url: 'https://services.swpc.noaa.gov', action, note: 'Connect HTTP node for live data' };
      break;
    case 'noaa_weather':
      result = { source: 'NOAA Weather', url: 'https://api.weather.gov', action, note: 'Connect HTTP node for live data' };
      break;
    case 'goes_satellite':
      result = { source: 'GOES Satellite', url: 'https://services.swpc.noaa.gov/json/goes', action, note: 'Connect HTTP node for live data' };
      break;
    case 'ace_satellite':
      result = { source: 'ACE Solar Wind', url: 'https://services.swpc.noaa.gov/products/ace', action, note: 'Connect HTTP node for live data' };
      break;
    case 'dscovr':
      result = { source: 'DSCOVR', url: 'https://services.swpc.noaa.gov/products/dscovr', action, note: 'Connect HTTP node for live data' };
      break;
    default:
      error = `Unknown space/weather integration: ${integration}`;
  }
} catch(e) {
  error = e.message;
}

return [{
  json: {
    success: !error,
    request_id: input.request_id,
    integration: integration,
    category: 'space_weather',
    action: action,
    data: result,
    error: error,
    timestamp: new Date().toISOString()
  }
}];
