// Route to specific environmental sensor integration
const input = $input.item.json;
const integrationName = input.integration?.integration || 'unknown';
const action = input.command?.action || 'default';
const params = input.command?.params || {};

return [{ 
  json: { 
    ...input, 
    routing_node: integrationName,
    action: action,
    params: params
  } 
}];

// Handle environmental sensor requests
const input = $input.item.json;
const integration = input.routing_node;
const action = input.action;
const params = input.params || {};

let result = null;
let error = null;

try {
  switch(integration) {
    case 'openaq':
      result = { source: 'OpenAQ', url: 'https://api.openaq.org/v2', action, note: 'Air quality data from global monitoring stations' };
      break;
    case 'epa_airnow':
      result = { source: 'EPA AirNow', url: 'https://www.airnowapi.org', action, note: 'US EPA air quality data' };
      break;
    case 'purpleair':
      result = { source: 'PurpleAir', url: 'https://api.purpleair.com/v1', action, note: 'Crowdsourced air quality sensors' };
      break;
    case 'iqair':
      result = { source: 'IQAir', url: 'https://api.airvisual.com/v2', action, note: 'Global air quality platform' };
      break;
    case 'safecast':
      result = { source: 'Safecast', url: 'https://api.safecast.org', action, note: 'Radiation monitoring network' };
      break;
    case 'breezometer':
      result = { source: 'BreezoMeter', url: 'https://api.breezometer.com', action, note: 'Air quality and pollen data' };
      break;
    case 'ambee':
      result = { source: 'Ambee', url: 'https://api.ambeedata.com', action, note: 'Environmental intelligence platform' };
      break;
    default:
      error = `Unknown environmental integration: ${integration}`;
  }
} catch(e) {
  error = e.message;
}

return [{
  json: {
    success: !error,
    request_id: input.request_id,
    integration: integration,
    category: 'environmental',
    action: action,
    data: result,
    error: error,
    timestamp: new Date().toISOString()
  }
}];
