// Prepare audit record
const crypto = require('crypto');
const input = $input.item.json;

// Extract command data
const command = input.command || {};
const integration = input.integration || {};
const result = input.result || input;

// Hash sensitive data
const paramsHash = crypto.createHash('sha256')
  .update(JSON.stringify(command.params || {}))
  .digest('hex');

const responseHash = crypto.createHash('sha256')
  .update(JSON.stringify(result))
  .digest('hex');

// Determine status
const status = result.success === true ? 'success' : result.success === false ? 'error' : 'unknown';

const auditRecord = {
  timestamp: new Date().toISOString(),
  request_id: command.request_id || input.request_id || $execution.id,
  actor: command.actor || 'system',
  integration: integration.integration || integration || 'unknown',
  action: command.action || 'unknown',
  category: integration.category || null,
  params_hash: paramsHash,
  response_hash: responseHash,
  status: status,
  duration_ms: input.duration_ms || null,
  error_message: result.error || result.message || null,
  risk_level: integration.risk || input.risk_level || 'read_only',
  confirmed: command.confirm === true,
  correlation_id: command.correlation_id || command.request_id || $execution.id,
  metadata: {
    execution_id: $execution.id,
    workflow_name: $workflow.name,
    workflow_id: $workflow.id
  }
};

return [{ json: auditRecord }];

// Return audit confirmation
const input = $('Execute Workflow Trigger').item.json;
const audit = $('Prepare Audit Record').item.json;

return [{
  json: {
    ...input,
    audit_logged: true,
    audit_request_id: audit.request_id,
    audit_timestamp: audit.timestamp
  }
}];
