// Route to specific automation platform
const input = $input.item.json;
const integrationName = input.integration?.integration || 'unknown';
const action = input.command?.action || 'default';
const params = input.command?.params || {};

return [{ 
  json: { 
    ...input, 
    routing_node: integrationName,
    action: action,
    params: params
  } 
}];

// Handle automation platform webhook triggers
const input = $input.item.json;
const integration = input.routing_node;
const action = input.action;
const params = input.params || {};

let result = null;
let error = null;

try {
  switch(integration) {
    case 'zapier':
      result = { source: 'Zapier', type: 'webhook', note: 'Trigger Zapier Zaps via webhook', webhook_url: params.webhook_url || 'Configure ZAPIER_WEBHOOK_URL' };
      break;
    case 'ifttt':
      result = { source: 'IFTTT', type: 'webhook', note: 'Trigger IFTTT applets via Webhooks service', webhook_url: params.webhook_url || 'Configure IFTTT_WEBHOOK_URL' };
      break;
    case 'make':
      result = { source: 'Make (Integromat)', type: 'webhook', note: 'Trigger Make scenarios via webhook', webhook_url: params.webhook_url || 'Configure MAKE_WEBHOOK_URL' };
      break;
    case 'pipedream':
      result = { source: 'Pipedream', type: 'webhook', note: 'Trigger Pipedream workflows', webhook_url: params.webhook_url || 'Configure PIPEDREAM_WEBHOOK_URL' };
      break;
    case 'activepieces':
      result = { source: 'Activepieces', type: 'webhook', note: 'Trigger Activepieces flows', webhook_url: params.webhook_url || 'Configure ACTIVEPIECES_WEBHOOK_URL' };
      break;
    default:
      error = `Unknown automation integration: ${integration}`;
  }
} catch(e) {
  error = e.message;
}

return [{
  json: {
    success: !error,
    request_id: input.request_id,
    integration: integration,
    category: 'automation',
    action: action,
    data: result,
    error: error,
    timestamp: new Date().toISOString()
  }
}];
