// Parse Proxmox command
const input = $input.item.json;
const command = input.command || {};
const action = command.action;

// Map actions to Proxmox API endpoints
const actionMap = {
  'inventory': { method: 'GET', endpoint: '/cluster/resources', risk: 'read' },
  'snapshot': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/snapshot', risk: 'write' },
  'restore': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/snapshot/{snapname}/rollback', risk: 'admin' },
  'status': { method: 'GET', endpoint: '/cluster/status', risk: 'read' },
  'vm_list': { method: 'GET', endpoint: '/nodes/{node}/qemu', risk: 'read' },
  'vm_start': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/status/start', risk: 'write' },
  'vm_stop': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/status/stop', risk: 'write' },
  'vm_shutdown': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/status/shutdown', risk: 'write' }
};

const actionConfig = actionMap[action];

if (!actionConfig) {
  return [{
    json: {
      error: true,
      message: `Unknown Proxmox action: ${action}`,
      request_id: command.request_id
    }
  }];
}

// Build endpoint with parameters
let endpoint = actionConfig.endpoint;
const params = command.params || {};

// Replace placeholders
endpoint = endpoint.replace('{node}', params.node || 'pve');
endpoint = endpoint.replace('{vmid}', params.vmid || '');
endpoint = endpoint.replace('{snapname}', params.snapname || '');

return [{
  json: {
    error: false,
    method: actionConfig.method,
    endpoint: endpoint,
    body: params.body || {},
    risk: actionConfig.risk,
    action: action,
    request_id: command.request_id,
    base_url: input.integration.base_url || 'https://proxmox.mycosoft.local:8006/api2/json'
  }
}];

// Format Proxmox response
const request = $('Parse Proxmox Command').item.json;
const response = $input.item.json;

return [{
  json: {
    success: true,
    request_id: request.request_id,
    integration: 'proxmox',
    action: request.action,
    data: response.data || response,
    timestamp: new Date().toISOString()
  }
}];

// Return error
const error = $input.item.json;
return [{ json: error }];
