// Parse Proxmox command with parameter validation
const input = $input.item.json;
const command = input.command || {};
const action = command.action;

// Map actions to Proxmox API endpoints with required parameters
const actionMap = {
  'inventory': { method: 'GET', endpoint: '/cluster/resources', risk: 'read', required: [] },
  'snapshot': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/snapshot', risk: 'write', required: ['vmid'] },
  'restore': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/snapshot/{snapname}/rollback', risk: 'admin', required: ['vmid', 'snapname'] },
  'status': { method: 'GET', endpoint: '/cluster/status', risk: 'read', required: [] },
  'vm_list': { method: 'GET', endpoint: '/nodes/{node}/qemu', risk: 'read', required: [] },
  'vm_start': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/status/start', risk: 'write', required: ['vmid'] },
  'vm_stop': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/status/stop', risk: 'write', required: ['vmid'] },
  'vm_shutdown': { method: 'POST', endpoint: '/nodes/{node}/qemu/{vmid}/status/shutdown', risk: 'write', required: ['vmid'] }
};

const actionConfig = actionMap[action];

if (!actionConfig) {
  return [{
    json: {
      error: true,
      message: `Unknown Proxmox action: ${action}. Available: ${Object.keys(actionMap).join(', ')}`,
      request_id: command.request_id
    }
  }];
}

const params = command.params || {};

// Validate required parameters (Bug 8 fix)
const missingParams = actionConfig.required.filter(p => !params[p]);
if (missingParams.length > 0) {
  return [{
    json: {
      error: true,
      message: `Missing required parameters for ${action}: ${missingParams.join(', ')}`,
      request_id: command.request_id,
      required_params: actionConfig.required
    }
  }];
}

// Build endpoint with parameters
let endpoint = actionConfig.endpoint;

// Replace placeholders (only if they exist in endpoint)
endpoint = endpoint.replace('{node}', params.node || 'pve');
if (params.vmid) endpoint = endpoint.replace('{vmid}', params.vmid);
if (params.snapname) endpoint = endpoint.replace('{snapname}', params.snapname);

// Final validation: ensure no unreplaced placeholders remain
if (endpoint.includes('{')) {
  return [{
    json: {
      error: true,
      message: `Endpoint still contains placeholders: ${endpoint}`,
      request_id: command.request_id
    }
  }];
}

return [{
  json: {
    error: false,
    method: actionConfig.method,
    endpoint: endpoint,
    body: params.body || {},
    risk: actionConfig.risk,
    action: action,
    request_id: command.request_id,
    base_url: input.integration.base_url || 'https://proxmox.mycosoft.local:8006/api2/json'
  }
}];

// Format Proxmox response with proper status code checking (Bug 7 fix)
const request = $('Parse Proxmox Command').item.json;
const response = $input.item.json;

// Check for HTTP errors - the httpRequest node includes statusCode in response
const statusCode = $input.item.statusCode || response.statusCode || 200;
const isError = statusCode >= 400;

// Also check for Proxmox-specific error indicators
const hasProxmoxError = response.errors || response.error || (response.data === null && statusCode !== 204);

const success = !isError && !hasProxmoxError;

if (!success) {
  return [{
    json: {
      success: false,
      error: true,
      request_id: request.request_id,
      integration: 'proxmox',
      action: request.action,
      status_code: statusCode,
      message: response.errors || response.message || response.error || `HTTP ${statusCode} error`,
      data: response,
      timestamp: new Date().toISOString()
    }
  }];
}

return [{
  json: {
    success: true,
    error: false,
    request_id: request.request_id,
    integration: 'proxmox',
    action: request.action,
    status_code: statusCode,
    data: response.data || response,
    timestamp: new Date().toISOString()
  }
}];

// Return error
const error = $input.item.json;
return [{ json: error }];
