// Parse UniFi command
const input = $input.item.json;
const command = input.command || {};
const action = command.action;

// Map actions to UniFi API endpoints
const actionMap = {
  'topology': { method: 'GET', endpoint: '/stat/device', risk: 'read' },
  'clients': { method: 'GET', endpoint: '/stat/sta', risk: 'read' },
  'networks': { method: 'GET', endpoint: '/list/networkconf', risk: 'read' },
  'traffic': { method: 'GET', endpoint: '/stat/report/daily.site', risk: 'read' },
  'device_list': { method: 'GET', endpoint: '/stat/device-basic', risk: 'read' },
  'health': { method: 'GET', endpoint: '/stat/health', risk: 'read' }
};

const actionConfig = actionMap[action];

if (!actionConfig) {
  return [{
    json: {
      error: true,
      message: `Unknown UniFi action: ${action}`,
      request_id: command.request_id
    }
  }];
}

const params = command.params || {};
const site = params.site || 'default';
const baseUrl = input.integration.base_url || 'https://unifi.mycosoft.local:8443';

return [{
  json: {
    error: false,
    method: actionConfig.method,
    endpoint: `/api/s/${site}${actionConfig.endpoint}`,
    risk: actionConfig.risk,
    action: action,
    request_id: command.request_id,
    base_url: baseUrl
  }
}];

// Format UniFi response
const request = $('Parse UniFi Command').item.json;
const response = $input.item.json;

return [{
  json: {
    success: true,
    request_id: request.request_id,
    integration: 'unifi',
    action: request.action,
    data: response.data || response,
    timestamp: new Date().toISOString()
  }
}];

// Return error
const error = $input.item.json;
return [{ json: error }];
