// Parse NAS health check command
const input = $input.item.json;
const command = input.command || {};
const action = command.action;

const actionMap = {
  'health': { operation: 'test_write', risk: 'write' },
  'read': { operation: 'read_test', risk: 'read' },
  'backup_verify': { operation: 'verify', risk: 'read' },
  'space_check': { operation: 'space', risk: 'read' }
};

const actionConfig = actionMap[action] || { operation: 'health', risk: 'read' };

return [{
  json: {
    operation: actionConfig.operation,
    risk: actionConfig.risk,
    action: action,
    request_id: command.request_id,
    nas_path: '/mnt/mycosoft-nas'
  }
}];

// Check disk space using shell command (placeholder)
// In production, you'd call a NAS API or use SSH
const request = $('Parse NAS Command').item.json;

return [{
  json: {
    success: true,
    request_id: request.request_id,
    integration: 'mycosoft_nas',
    action: request.action,
    data: {
      read_test: 'passed',
      write_test: 'passed',
      timestamp: new Date().toISOString()
    }
  }
}];
