// Parse GPU job command
const input = $input.item.json;
const command = input.command || {};
const params = command.params || {};

const jobRequest = {
  job_type: params.job_type || 'inference',
  model: params.model || 'default',
  input_data: params.input_data || {},
  priority: params.priority || 'normal',
  request_id: command.request_id,
  submitted_by: command.actor,
  submitted_at: new Date().toISOString()
};

return [{
  json: {
    job: jobRequest,
    action: command.action,
    request_id: command.request_id,
    gpu_endpoint: input.integration.base_url || 'http://localhost:9000/api'
  }
}];

// Format GPU job response
const request = $('Prepare GPU Job').item.json;
const response = $input.item.json;

return [{
  json: {
    success: true,
    request_id: request.request_id,
    integration: 'myca_gpu_runner',
    action: request.action,
    data: {
      job_id: response.job_id || response.id,
      status: response.status || 'queued',
      submitted_at: request.job.submitted_at
    },
    timestamp: new Date().toISOString()
  }
}];
