// Parse UART command
const input = $input.item.json;
const command = input.command || {};
const params = command.params || {};

const lines = params.lines || 100;
const filter = params.filter || null;

return [{
  json: {
    action: command.action,
    request_id: command.request_id,
    lines: lines,
    filter: filter,
    uart_endpoint: input.integration.base_url || 'http://localhost:8080/uart'
  }
}];

// Format UART response
const request = $('Parse UART Command').item.json;
const response = $input.item.json;

// Apply filter if specified
let lines = response.lines || [];
if (request.filter) {
  lines = lines.filter(line => line.includes(request.filter));
}

return [{
  json: {
    success: true,
    request_id: request.request_id,
    integration: 'myca_uart',
    action: request.action,
    data: {
      lines: lines,
      count: lines.length,
      filter_applied: request.filter || null
    },
    timestamp: new Date().toISOString()
  }
}];
