// PersonaPlex Voice Gateway - Route voice/text to MYCA
const input = $input.first().json;
const startTime = Date.now();

// Extract voice/text data
const transcript = input.transcript || input.message || input.text || '';
const audioData = input.audio_base64 || null;
const sessionId = input.session_id || `pp_${Date.now()}`;
const voiceId = input.voice_id || 'NATURAL_F2';
const modality = input.modality || (audioData ? 'voice' : 'text');

// Conversation context
const context = {
  session_id: sessionId,
  voice_id: voiceId,
  modality: modality,
  has_audio: !!audioData,
  timestamp: new Date().toISOString(),
  latency_start: startTime
};

return {
  json: {
    transcript: transcript,
    audio_base64: audioData,
    session_id: sessionId,
    voice_id: voiceId,
    modality: modality,
    context: context,
    route_to: 'myca_orchestrator'
  }
};

// Process MYCA response for PersonaPlex
const mycaResponse = $input.first().json;
const context = $('Parse Voice Input').first().json.context;
const latency = Date.now() - context.latency_start;

const responseText = mycaResponse.response_text || mycaResponse.response || '';
const agentName = mycaResponse.agent_name || 'MYCA';
const actionsTaken = mycaResponse.actions_taken || [];

// Log actions for debugging
const actionLog = actionsTaken.map(a => `${a.type}: ${JSON.stringify(a.detail)}`).join(', ');

return {
  json: {
    status: 'success',
    session_id: context.session_id,
    response_text: responseText,
    agent_name: agentName,
    voice_id: context.voice_id,
    modality: context.modality,
    latency_ms: latency,
    actions_taken: actionsTaken,
    action_log: actionLog,
    conversation_id: mycaResponse.conversation_id,
    timestamp: new Date().toISOString()
  }
};
