// MYCA Orchestrator - Parse and route commands
const body = $input.item.json.body || $input.item.json;

// Extract command info
const text = body.text || body.message || body.query || body.command || '';
const audio = $input.item.binary?.data;
const action = body.action || 'chat';

// Determine intent from action or keywords
let intent = action;
if (text) {
  const lowerText = text.toLowerCase();
  if (lowerText.includes('agent') || lowerText.includes('worker')) intent = 'agent_management';
  else if (lowerText.includes('system') || lowerText.includes('health') || lowerText.includes('status')) intent = 'system_status';
  else if (lowerText.includes('network') || lowerText.includes('server') || lowerText.includes('proxmox') || lowerText.includes('unifi')) intent = 'infrastructure';
  else if (lowerText.includes('task') || lowerText.includes('job') || lowerText.includes('execute')) intent = 'task_execution';
  else intent = 'chat';
}

return {
  intent,
  text,
  has_audio: !!audio,
  request_id: body.request_id || `req_${Date.now()}`,
  user_id: body.user_id || 'anonymous',
  timestamp: new Date().toISOString(),
  raw_body: body
};

// Format response from MAS
const masResponse = $input.item.json;
const prevData = $('Parse Request').first().json;

let response_text = '';
let data = null;

if (masResponse.response) {
  response_text = masResponse.response;
} else if (masResponse.message) {
  response_text = masResponse.message;
} else if (masResponse.error) {
  response_text = `Error: ${masResponse.error}`;
} else {
  response_text = JSON.stringify(masResponse);
}

return {
  status: 'success',
  request_id: prevData.request_id,
  intent: prevData.intent,
  transcript: prevData.text,
  response_text,
  data: masResponse.data || null,
  timestamp: new Date().toISOString()
};
