// Format the response
const input = $input.all()[0].json;
const aiResponse = input.message?.content || input.choices?.[0]?.message?.content || 'I apologize, but I could not generate a response.';

return {
  response: aiResponse,
  agent: 'myca-orchestrator',
  provider: 'openai-n8n',
  timestamp: new Date().toISOString(),
  workflow: 'myca-chat-workflow'
};
